INSERT INTO `news` VALUES ('2', '/WEB-INF/upload/f/0/d/c/3/8/3/7/680a6ecb-658f-4c64-9f1c-234a87572b79_news_4.jpg', '2018年CRC狂犬病防治项目进度报告', '2018-06-01 00:00:00', '2018年，为落实爱护动物的理念，关爱流浪动物健康，中国小动物保护协会携手默沙东，联合全国知名动物医院连锁品牌，再次启动CRC狂犬病防治项目。让我们携手实现一个没有狂犬病的世界！​​​​', 'news_Center_content1.jsp', );
INSERT INTO `news` VALUES ('4', '/WEB-INF/upload/1/1/8/6/6/6/4/7/97dae442-40ea-44e9-aeb1-7359f5f5f040_news_6.jpg', '『生命关爱教育公益大讲堂』', '2018-09-15 00:00:00', '2018年中国小动物保护协会联合中国儿童少年基金会少年公益学院，开启了“尊重生命 宠爱童行——儿童宠物关爱计划”，为城市儿童的安全与善良护航！', 'news_Center_content1.jsp', );
INSERT INTO `news` VALUES ('5', '/WEB-INF/upload/5/2/8/7/e/e/9/7/c50276d8-ff08-4449-95e5-c924d25695f2_news_8.jpg', '不一样的儿童节活动，伴侣动物文化展', '2018-06-05 00:00:00', 'CSAPA伴侣动物文化展这次来到北京市大兴区世界之花假日广场，本次活动融合了公益爱心市集、流浪狗态度领养及儿童善良教育系列活动，虽然天气很热，广场上依然人声鼎沸，热闹非凡。', 'news_Center_content1.jsp', );
